$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_1200a9164ff0d650c5b1b0904a74398c'] = 'Frequently Asked Questions (Click to expand)';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_a8da2ca4ca09ffe00000d46da7e995af'] = '1. Is it compulsary to use CRON?';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_b14c240dbed0f1210b2ac554936e6e0e'] = 'We understand that you may be uncomfortable with CRON configuration therefore this module has an option to update abandoned cart list and send reminder emails manually. But we recommend all to use CRON as in case of large amount of abandoned carts, it is not possible for admin to send emails manually.';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_eff86ce34f4a739ff555036ab6f7101d'] = '2. How will I know if some customer places order using coupon code from abandoned cart email?';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_6c7ca1ce8e7ab2491e99ed3406a93dc8'] = 'You can check complete order information of such customers in \"Converted lists\" tab of our admin panel.';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_28727f700ceb02e82f02249fa100bd1d'] = '3. Is it compulsary to enable \"Redisplay cart at login\"?';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_dcfa8eb88fb8024263c6f630cd9d37f1'] = 'No, it is not compulsary but there is a drawback and 0 benefit for not enabling it therefore it is highly recommended to enable it.';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_128f6bce42c3eb12dfeed41faaaddfd8'] = 'If a customer logins to your store, he will not find his previously added products in the cart and everytime he abandones a cart, a new entry will be made for him in abandoned cart list.';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_d52dd9d760e4dc9d68cbf1da18e002f5'] = '4. How many serial reminders should I setup for best result?';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_6177df7e9fa2b783a8aa7f39e02cbbdf'] = 'It has been found that three abandoned cart messages increased revenue by 56 percent, versus sending just one but there is no hard and fast rule for this. Testing is always a best way to determine what fits best for you.';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_4ace9425d75464a2110001a6096acdbb'] = '5.  I need your help, can I contact you?';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_9d32d737e383d7638ebb142f5fef03cd'] = 'Yes, if you have any query or facing some issue, do not hesitate to raise a ticket for the same.';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_41ecca7200a4431cf9b855a62e6ab666'] = 'Suggestions';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_d5704522b597f94fc67f40986259508d'] = 'Want us to include some feature in next version of this module?';
$_MODULE['<{abandonedcart}default-bootstrap>abandonedcart_14ee705e87fdb338ef7f2a1b3863e2c1'] = 'If you like our module, do not forget to give us 5 STAR rating on the above link. This will definitely boost our morale.';
