$_MODULE['<{envoimoinscher}default-bootstrap>getcontenthelp_0ff8b1eee8ee71d7e6fe6ff600fc3d39'] = 'Un bloc sur le haut de la page vous indique le traitement des envois (expéditions traitées et en cours). Vous pouvez à tout moment sortir et interrompre l\'envoi en masse en cliquant sur « annuler les envois restants » dans ce bloc.';
$_MODULE['<{envoimoinscher}default-bootstrap>getcontenthelp_7a71174359829f7790bd0a3fb11a158d'] = 'Comment expédier vos commandes sans transporteur EnvoiMoinsCher sélectionné ?';
