$_MODULE['<{envoimoinscher}default-bootstrap>getcontenthelp_6af0d19437897649ae8e5fa156be81d8'] = 'Pour la description anglaise, vous pouvez utiliser le lien « Traduire » mis à votre disposition. Une traduction Google vous sera proposée.';
