$_MODULE['<{envoimoinscher}default-bootstrap>getcontenthelp_4c6e6184add527641c6ec79fd18ea183'] = 'Vous devez joindre à votre colis une facture commerciale en 5 exemplaires pour tous les envois destinés à l\'international (hors UE).';
