$_MODULE['<{envoimoinscher}default-bootstrap>getcontenthelp_102e1d99cfdfdcc458bef649e6216830'] = 'A partir de Prestashop';
$_MODULE['<{envoimoinscher}default-bootstrap>getcontenthelp_633663db2e17013bc5546bc649344cf0'] = 'Allez dans l\'onglet « commandes », sous-onglet « envoimoinscher » dans « historique des envois traités ». Pour télécharger un bordereau : cliquez sur « télécharger bordereau » à l\'extrême droite de la ligne de votre commande. Pour télécharger plusieurs bordereaux issus de commandes différentes : cochez les cases des commandes dont vous souhaitez les bordereaux et cliquez sur « télécharger ».';
$_MODULE['<{envoimoinscher}default-bootstrap>getcontenthelp_78dff771ce5a3edbaaff7daa0f809ca5'] = 'Sur votre adresse email';
